function lightbox(e,t){if($("#lightbox").size()==0){var n=$('<div id="lightbox"/>');var r=$('<div id="lightbox-shadow"/>');$(r).click(function(e){});$("body").append(r);$("body").append(n)}$("#lightbox").empty();if(e!=null){$("#lightbox").append(e)}if(t!=null){$("#lightbox").append('<p class="loading">Loading...</p>');$.ajax({type:"GET",url:t,success:function(e){$("#lightbox").empty();$("#lightbox").append(e)},error:function(){alert("AJAX Failure!")}})}$("#lightbox").css("top",$(window).scrollTop()+100+"px");$("#lightbox").show();$("#lightbox-shadow").show()}function closeLightbox(){$("#lightbox").hide();$("#lightbox-shadow").hide();$("#lightbox").empty()}